mapper.select:
SELECT * FROM
test_subjects left join chapters on test_subjects.subject_id =
chapters.subject;

mapper.update:
update test_questions
set question_img_url = '${questionImageUrl}', solution_img_url =
'${solutionImageUrl}' where id = ${id}

mapper.select:
SELECT * FROM test_questions WHERE 

subject_id = ${subjectId} AND
chapter = ${chapter.chapterId}

	AND level between ${level - 1} AND ${level}


	AND question_type = '${type}'

ORDER BY RAND() LIMIT 1;


	id = '${id}'

update.foreach:
update test_questions
question = '${question.question}', option1 = '${question.option1}', option2 = '${question.option2}', option3 = '${question.option3}', option4 = '${question.option4}', solution = '${question.solution}'
where id = ${question.id}

mapper.update:
update test_questions
question = '${question}', option1 = '${option1}', option2 = '${option2}', option3 = '${option3}', option4 = '${option4}', solution = '${solution}'
where id = ${id}
