mapper.select:
SELECT * FROM test_subjects left join chapters on test_subjects.subject_id = chapters.subject
where (test_subjects.institute_id is null OR test_subjects.institute_id = ${value}) AND (chapters.institute_id = ${value} OR chapters.institute_id IS NULL) AND (chapters.status != 'D' OR chapters.status is null)
;

SELECT * FROM test_questions WHERE 

subject_id = ${subjectId} AND
chapter = ${chapter.chapterId}

	AND level between ${level - 1} AND ${level}


	AND question_type = '${type}'


	AND (question_type != '${type}' OR question_type IS NULL)

AND length(trim(option1)) > 0 AND (status IS NULL or status = 'A')
ORDER BY RAND() LIMIT ${questionNumber};


	id = '${id}'

update.foreach:
update test_questions
question = '${question.question}', option1 = '${question.option1}', option2 = '${question.option2}', option3 = '${question.option3}', option4 = '${question.option4}', solution = '${question.solution}'
where id = ${question.id}

mapper.update:
update test_questions
question = '${question}', option1 = '${option1}', option2 = '${option2}', option3 = '${option3}', option4 = '${option4}', solution = '${solution}'
where id = ${id}

update exam_feedback set resolution = '${resolution}', answered_by = ${studentId},
feedback_resolution_text = '${feedbackResolutionText}'

 	, feedback_resolution_video_url = '${feedbackVideoUrl}'

where 1

	AND question_id = ${questionId}


	AND video_id = ${videoId}


	AND id = ${id}


AND resolution is NULL

mapper.select:
SELECT
count(*) as frequency,
exam_feedback.created_date,video_lectures.subject_id,exam_feedback.type,resolution,video_id,video_url,video_name, feedback_resolution_text, feedback_resolution_image_url, test_subjects.subject,chapter_name, updated_date, feedback
,student.name as answeredBy,exam_feedback.id as feedbackId,attachment_link
from exam_feedback join video_lectures on exam_feedback.video_id = video_lectures.id left join test_subjects on test_subjects.subject_id = exam_feedback.subject_id left join chapters on exam_feedback.chapter_id = chapters.id
left join student on exam_feedback.answered_by = student.id
where 1
AND resolution is NULL
AND resolution is NOT NULL
AND exam_feedback.created_date between '${fromDate}' AND '${toDate}'
AND student_id = ${student.id}
AND exam_feedback.subject_id = ${subjectId}
AND exam_feedback.chapter_id = ${question.chapter.chapterId}
AND exam_feedback.institute_id = ${institute.id}
group by video_id
order by exam_feedback.created_date desc;

SELECT feedback_resolution_text,feedback_resolution_video_url, exam_feedback.subject_Id, exam_feedback.created_date,feedback_resolution_text, test_subjects.subject,chapter_name, updated_date, feedback_resolution_image_url, feedback ,student.name as answeredBy, exam_feedback.id as feedbackId,exam_feedback.id as id,attachment_link,askedBy.name as askedBy,resolution FROM exam_feedback join student as askedBy on exam_feedback.student_id = askedBy.id left join test_subjects on test_subjects.subject_id = exam_feedback.subject_id left join chapters on exam_feedback.chapter_id = chapters.id left join student on exam_feedback.answered_by = student.id where 1
AND resolution is NULL
AND resolution is NOT NULL
AND exam_feedback.created_date between '${fromDate}' AND '${toDate}'
AND student_id = ${student.id}
AND student_id in (select student_id from student_institute where institute_id = ${institute.id} AND package_id in ( select package_id from student_institute where student_id = ${student.id}))
AND exam_feedback.subject_id = ${subjectId}
AND exam_feedback.chapter_id = ${question.chapter.chapterId}
AND exam_feedback.institute_id = ${institute.id} AND video_id is null AND question_id is NULL order by exam_feedback.created_date desc;

select count(
distinct question_id
distinct video_id
id
) as frequency from exam_feedback where 1
AND resolution is NULL
AND resolution is NOT NULL
AND exam_feedback.created_date between '${fromDate}' AND '${toDate}'
AND student_id = ${student.id}
AND student_id in (select student_id from student_institute where institute_id = ${institute.id} AND package_id in ( select package_id from student_institute where student_id = ${student.id}))
AND exam_feedback.subject_id = ${subjectId}
AND exam_feedback.chapter_id = ${question.chapter.chapterId}
AND exam_feedback.institute_id = ${institute.id}
AND question_id is NOT NULL
AND video_id is NOT NULL
AND video_id is null AND question_id is NULL

select * from exam_feedback 
left join student on student.id = exam_feedback.answered_by
where 1

	AND question_id = ${questionId}


	AND video_id = ${videoId}


	AND id = ${id}


	AND resolution = '${resolution}'


	AND (resolution IS NULL or resolution = 'Unresolved')

LIMIT 1

select exam_feedback.id as feedbackId,exam_feedback.feedback,exam_feedback.created_date as feedbackDate,exam_feedback.resolution,exam_feedback.question_id, exam_feedback.video_id,exam_feedback.subject_id,exam_feedback.student_id,exam_feedback.feedback_resolution_text,student.name as studentName,test_questions.status as questionStatus, test_questions.question,test_questions.question_img_url,video_lectures.video_name,video_lectures.video_url,test_subjects.subject,solvedBy.name as teacherName,attachment_link,feedback_resolution_image_url from exam_feedback left join student on exam_feedback.student_id = student.id left join test_questions on test_questions.id = exam_feedback.question_id left join video_lectures on video_lectures.id = exam_feedback.video_id left join test_subjects on test_subjects.subject_id = exam_feedback.subject_id left join student as solvedBy on exam_feedback.answered_by = solvedBy.id where
exam_feedback.video_id = ${id}
exam_feedback.question_id = ${id}
AND exam_feedback.resolution is NULL
AND exam_feedback.resolution is NOT NULL
order by exam_feedback.created_date desc;


